import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './card.css';

function BasicExample() {
  const [fileContent, setFileContent] = useState(""); // State to store file content

  useEffect(() => {
    // Fetch the file when the component mounts
    fetch('../../transcript.txt')
      .then(response => response.text())
      .then(data => setFileContent(data));
  }, []);

  return (
    <Card className="custom-card">
      <Card.Body>
        <Card.Title>Transcription</Card.Title>
        <Card.Text>
          {fileContent} {/* Display the file content */}
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default BasicExample;
