import React from "react";
import { Layout } from "antd";
import DashboardCard from "../../UI Components/Card/DashboardCard";
import StudentPageLayout from "../Layout/StudentPageLayout";

import StudentClasses from "../StudentHome.module.css";
import cohere from "cohere-ai";

const { Content } = Layout;

class SiderDemo extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      response: null,
      error: null
    };
    this.handleGenerateClick = this.handleGenerateClick.bind(this);
  }
  handleGenerateClick() {
    const requestOptions = {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${your_api_key}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        prompt: "Write a sentence about a cat",
        model: "text-davinci-002"
      })
    };

    fetch('https://api.cohere.ai/v1/generate', requestOptions)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.setState({ response: data });
      })
      .catch(error => {
        console.error(error);
        this.setState({ error: error });
      });
  }
  handleAudioUpload = (event) => {
    const file = event.target.files[0];
    console.log('Selected file:', file);
    //upload it to a server
  };


  render() {
    return (
      <StudentPageLayout menuSelect="1">
        <Content className={StudentClasses.dashboard}>
        <label htmlFor="audio-upload">Upload Audio File:</label>
                   <input type="file" id="audio-upload" onChange={this.handleAudioUpload} />

         {/* <DashboardCard
            imageURL="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQwmgYEUwvWfI5ivwD5eXaBGzR35wdzrjI-B1MDYIBK9PJBSrwF-A"
            title="Assignments"
            link="/assignments"
    />*/}
        <div>
      <button onChange={this.handleClick}>Cohere API call</button>
    </div>
    <div>
        <button onClick={this.handleGenerateClick}>Generate text</button>
        <h1>Response:</h1>
        <pre>{JSON.stringify(this.state.response, null, 2)}</pre>
        <h1>Error:</h1>
        <pre>{JSON.stringify(this.state.error, null, 2)}</pre>
      </div>

        </Content>
      </StudentPageLayout>
    );
  }
}

export default SiderDemo;
