import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './card.css'; // Import CSS file

function BasicExample() {
  const [transcription, setTranscription] = useState(""); // Store file contents in state

  // Function to handle file selection
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (event) => {
      const contents = event.target.result;
      setTranscription(contents);
    };
    reader.readAsText(file);
  }

  return (
    <Card className="custom-card"> {/* Add custom class name */}
      <Card.Body>
        <Card.Title>Transcription</Card.Title>
        <Card.Text>{transcription}</Card.Text> {/* Display file contents */}
        <Button variant="primary" onClick={() => {document.getElementById('fileInput').click();}}>Select File</Button> {/* Button to select file */}
        <input id="fileInput" type="file" style={{display: 'none'}} onChange={handleFileSelect} /> {/* Hidden file input element */}
      </Card.Body>
    </Card>
  );
}

export default BasicExample;
